package com.lxj.leetcode.hashtable;

import java.util.HashSet;

/**
 * @author Xingjing.Li
 * @since 2023/1/28
 */
public class GreatestEnglishLetterInUpperAndLowerCase2309 {

    public String greatestLetter(String s) {
        HashSet<Character> set = new HashSet<>();
        int n = s.length();
        for (int i = 0; i < n; i++) {
            set.add(s.charAt(i));
        }
        for (int i = 25; i >= 0; i--) {
            if (set.contains((char)('a'+i)) && set.contains((char)('A'+i))){
                return String.valueOf((char)('A'+i));
            }
        }
        return "";
    }

    public static void main(String[] args) {
        GreatestEnglishLetterInUpperAndLowerCase2309 solution = new GreatestEnglishLetterInUpperAndLowerCase2309();
//        System.out.println(solution.greatestLetter("arRAzFif"));
        System.out.println(solution.greatestLetter(""));
    }
}
